            //     fillColor: "#49009e",
            //     fillOpacity: 1,
            //     // strokeColor: '#000',
            //     // strokeWeight: 2,
            //     // scale: 1,
                   
            };

            var marker = new google.maps.Marker({
                position : latlng,
                map : map,
                title: title,
                full_address: full_address,
                storeLat: storeLat,
                storeLng: storeLng,
                icon: icon,
                direct_address : direct_address,
                // icon: iconchange("#49009e")
            });
